import React from "react";
import { Link } from "react-router-dom";
import '../assets/style/Games.css'
import { Navbar } from '../components'

type gameType = {
    id: string,
    name: string,
    logo: string,
    info: string,
    min: number,
    max: number
}

export default function Games(){

    const games = [
        {"name": "Secret Hitler", "min": 5, "max": 10, "info": "Divided in two teams, facists and liberals. As a liberal you want to find out who the facists are, and as facist you want to lie yourself to a victory."
        , "id":"SecretHitler", "logo": "https://cdn.shopify.com/s/files/1/1915/6645/products/secret-hitler-in-wooden-box-kickstarter-special-kickstarter-board-game-goat-wolf-cabbage-21574739080_2000x.png?v=1576285397"
        },
        {"name": "Castle Wars", "min": 2, "max": 2, "info": "A card game in which you must find the balance of attacking your opponent's castle, defending your castle and managing you resources."
        , "id":"CastleWars", "logo": "https://jefawk.com/assets/img/games/castlewars/castlewars-logo-256x256.jpg"
        },
        {"name": "Maffia", "min": 7, "max": 24, "info": " Two teams compete in this delightfully deadly party game. Based on the classic role-playing strategy game, it's kill or be killed as innocent city folk pit their wits against a mob of ruthless international gangsters."
        , "id":"Maffia", "logo": "https://jefawk.com/assets/img/games/castlewars/castlewars-logo-256x256.jpg"
        }
    ]

    const JoinGame = (id:string):void => {
        alert("Fuck you " + id)
    }

    function gameDiv(game: gameType){
        return <div className="game">
        <img src={game.logo} alt="SH" />
        <h1>{game.name}</h1>
        <p className="gameplayers">{game.min === game.max ? game.max : game.min + ' - ' + game.max} players</p>
        <p className="gameinfo">{game.info}</p>
        <p><button onClick={() => JoinGame(game.id)}>Join Game</button></p>
        <p><button>Create Game</button></p>
      </div>
    }

    return <>
    <Navbar></Navbar>
    <div className="gameslist">
        {games.map(game => {
            return gameDiv(game)
        })}
    </div>        
    </>
}
