import React, { useState, useRef } from "react";
//import { Radar } from "react-chartjs-2";
import usePlayers from "../../hooks/skrallgaming/usePlayers";
import playersArray from "../../globals/skrallgaming/players"
//import placings from "../../globals/skrallgaming/placings"
import "../../assets/style/skrallgaming/createThornament.css"

export default function CreateThornament(){

    const { players, setPlayers } = usePlayers()
    const [ teams, setTeams ] = useState([])

    const tournamentName = useRef('SSE-II 2022 Smash Doubles')
    const gameFormat = useRef('2v2')
    const game = useRef('smash')

    const groupStage = useRef(true)
    const groupMaxTeams  = useRef(4)
    const groupAdvance = useRef(2)

    const eliminitationType = useRef(2)

    const teamName = useRef()
    const p1 = useRef()
    const p2 = useRef()

    useState(() => {if (!players?.lenght) setPlayers(playersArray)}, [])

    const registerTeam = () =>{
        if (!p1.current.value || !p2.current.value || !teamName.current.value) return
        let arr = [...[], ...teams]
        arr.push({
            name: teamName.current.value,
            players: [p1.current.value, p2.current.value]
        })
        setTeams(arr);
    }

    const removeTeam = (i) => {
        let arr = [...[], ...teams]
        arr.splice(i, 1)
        setTeams(arr)
    }    

    const startThornament = () => {
        if (!teams) return alert("Register teams first!")
        let matches = []
        let uBracketMatches_C = (teams.length - 1)
        let uBracketTeams_C = teams.length;
        let groups = []

        //Create all group matches
        if (groupStage.current.value){
            groups = Array(Math.ceil(teams.length / groupMaxTeams.current.value))
            let groupId = 0
            for (let i = 0; i < teams.length; i++){
                if (!groups[groupId]) groups[groupId] = []
                groups[groupId] = [...groups?.[groupId], teams[i]]
                groupId++
                if (groupId === groups.length) groupId = 0
            }
            console.log("Groups:", groups)

            groups.map((group, i) => {
                group.map((team, j) => {
                    for(let k = j+1; k < group.length; k++){
                        matches.push({
                            "type":"groupmatch",
                            "group": String.fromCharCode(65+i),
                            "team1": group[j].name,
                            "team2": group[k].name,
                        })
                    }
                })                
            })
            uBracketTeams_C = (groupAdvance.current.value * groups.length)
            uBracketMatches_C = uBracketTeams_C - 1
        }

        if (eliminitationType.current.value === 1) uBracketMatches_C += 1 //Bronze Match for single elimination

        const getRounds = (int) => {            
            if (typeof int !== "number") return
            let i = 0
            let ret = [];
            
            while (int > 0){
                let rm = Math.pow(2, i)
                if (int >= rm) ret.unshift(rm)
                else ret.unshift(int)
                int -= rm
                i++                
            }

            return ret
        }
        
        const getOpponent = (id, type) => {
            if (id > 1) return id-1            
            return (type === "group" ? groups.length: groupAdvance.current.value)
        }        

        const getRestMatches = (arr, restMatches, rounds) => {
            let r = [];
            if (arr === null){
                arr = [];
                for(let i = 1; i < rounds; i++){
                    arr.push(i);
                }
                r.push(1)
                restMatches--
            }
            let arr2 = arr.splice(arr.lenght / 2)
            r.push(arr2[0])
            console.log("r", r, restMatches)
            restMatches--
            if(restMatches > 0)
                r = [...r, ...getRestMatches(arr, Math.ceil(restMatches/2))]
            restMatches--
            if(restMatches > 0)
                r = [...r, ...getRestMatches(arr2, Math.floor(restMatches/2))]
            return r.sort((a, b) => a - b);
        }                
     
        let rounds = getRounds(uBracketMatches_C)                
        let restMatches = getRestMatches(null, rounds[1], uBracketMatches_C)
        console.log(`Teams left: ${uBracketTeams_C}`)
        console.log(`Upper Bracket Matches left: ${uBracketMatches_C}`)
        console.log("Rounds: ", rounds)
        console.log("restMatches: ", restMatches)

        let upperBracket = []
        let groupId = groups.length
        let advanceId = groupAdvance.current.value
        rounds.map((r, i) => {
            if (i>0)return
            for(let j = 0; j < r; j++){
                if(i === 0){                    
                    upperBracket.push({
                        "team1": {group: groupId, placement: advanceId},
                        "team2": {group: getOpponent(groupId, "group"), placement: getOpponent(advanceId, "placement")},
                        "round": i,
                        "match": upperBracket.length+1
                    })
                }
                groupId -= 1
                advanceId -= 1
                uBracketMatches_C--;
                uBracketTeams_C -= 2;
                if (advanceId === 0) advanceId += groups.lenght
                if (groupId === 0) groupId += groups.lenght
            }
        })
        console.log(`UBracket Matches Left: ${uBracketMatches_C}`)
        console.log(`UBracket Teams Left: ${uBracketTeams_C}`)
        console.log("upperBracket:", upperBracket)
    }

    return <>        
        <div className="CreateThornamentBody">
            <div>                
                <span>
                    <fieldset className="part">
                        <legend>Tournament Info</legend>
                        <div className="inputBox">
                            <input id="tournamentName" required 
                                ref={tournamentName} 
                            /> 
                            <span>Tournament Name</span>
                        </div>
                        <div className="inputBox">
                            <select id="gameFormat" ref={gameFormat}>
                                <option value="1v1">1v1</option>
                                <option value="2v2">2v2</option>
                            </select>
                            <span>Game Format</span>
                        </div>
                        <div className="inputBox">
                            <select id="game" ref={game}>
                                <option value="smash">Smash Ultimate</option>
                                <option value="beerPong">Beerpong</option>
                            </select>
                            <span>Game</span>
                        </div>                    
                        <div>
                            <input type="checkbox" id="groupStage" ref={groupStage.current.value}/>
                            <label htmlFor="groupStage">Groupstage?</label>
                        </div>                    
                        {groupStage.current.value &&<>
                            <div className="inputBox">
                                <input type="number" id="maxTeamsPerGrp" required ref={groupMaxTeams.current.value}/> 
                                <span>Max teams per group</span>
                            </div>
                            <div className="inputBox">
                                <input type="number" id="advance" required 
                                    ref={groupAdvance} max={groupMaxTeams.current.value} min={1} 
                                /> 
                                <span>Teams advance from each group</span>
                            </div>
                        </>} 
                        <div className="inputBox">
                            <select required id="eliminitationType" ref={eliminitationType}>
                                <option value={1}>Single Elimination</option>
                                <option value={2}>Double Elimination</option>
                            </select>
                            <span>Elimination type</span>
                        </div>
                        <div>
                            <input type="checkbox" id="eloStructure"/>
                            <label htmlFor="eloStructure">Structure by Elo</label>
                        </div>                    
                    </fieldset>
                </span>
                <span>
                    <fieldset className="part">
                        <legend>Team Registration</legend>                                            
                        <div className="inputBox">
                            <select id="p1" defaultValue={""} ref={p1}>
                                <option value="" disabled/>
                                {players &&
                                    players.map(p => <option value={p?.name}>{p?.name}</option>)
                                }
                            </select>
                            <span>Player 1</span>
                        </div>
                        <div className="inputBox">
                            <select id="p2" defaultValue={""} ref={p2}>
                                <option value="" disabled/>
                                {players &&
                                    players.map(p => <option value={p?.name}>{p?.name}</option>)
                                }
                            </select>
                            <span>Player 2</span>
                        </div>
                        <div className="inputBox">
                            <input id="teamName" required 
                                ref={teamName}
                                onKeyDown={(k) => {if(k.key === "Enter") registerTeam()}} 
                            /> 
                            <span>Team name</span>
                        </div>
                        <button onClick={() => registerTeam()}>Register Team</button>
                    </fieldset>
                </span>
                <span>
                    <fieldset>
                        <legend>Registered Teams</legend>
                        <table className="registeredTeams">
                            <thead><tr>
                                <th></th>
                                <th>Team</th>
                                <th>Players</th>
                            </tr></thead>
                            <tbody>
                                {teams &&
                                    teams.map((t, i) => <tr>
                                        <td>{i+1}</td>
                                        <td>{t.name}</td>
                                        <td>{t.players.join(' & ')}</td>
                                        <td><i onClick={() => removeTeam(i)}>❌</i></td>
                                    </tr>)
                                }
                            </tbody>
                        </table>
                    </fieldset>
                </span>
            </div>            
            {
                teams &&
                <div className="finishButton" onClick={() => startThornament()}>Hold to start Thornament</div>
            }
        </div>    
    </>
}
