import React from "react";
import { Link } from "react-router-dom";
import '../assets/style/Games.css'
import { Navbar } from '../components'

type gameType = {
    id: string,
    name: string,
    logo: string,
    info: string,
    min: number,
    max: number
}

export default function Games(){

    const games = [
        {"name": "Secret Hitler", "min": 5, "max": 10, "info": "A game divided in two teams, facists and liberals."
        , "id":"SH", "logo": "https://cdn.shopify.com/s/files/1/1915/6645/products/secret-hitler-in-wooden-box-kickstarter-special-kickstarter-board-game-goat-wolf-cabbage-21574739080_2000x.png?v=1576285397"
        },
        {"name": "Castle Wars", "min": 2, "max": 2, "info": "abcsabd asdasd asdasd asdasda dsad asd asd asd"
        , "id":"CW", "logo": "https://jefawk.com/assets/img/games/castlewars/castlewars-logo-256x256.jpg"
        }
    ]

    function gameDiv(game: gameType){
        return <div className="game">
        <img src={game.logo} alt="SH" />
        <h1>{game.name}</h1>
        <p className="players">{game.min === game.max ? game.max : game.min + ' - ' + game.max} players</p>
        <p>{game.info}</p>
        <p><button>Join Game</button></p>
        <p><button>Create Game</button></p>
      </div>
    }

    return <>
    <Navbar></Navbar>
    <div className="gameslist">
        {games.map(game => {
            return gameDiv(game)
        })}
    </div>        
    </>
}
