import React, { useEffect, useState } from "react";
import usePlayers from "../../hooks/skrallgaming/usePlayers";
import playersArray from "../../globals/skrallgaming/players"
import "../../assets/style/skrallgaming/playerlist.css"

export default function PlayerList(){

    const { players, setPlayers } = usePlayers()
    const [ currentSortBy, setCurrentSortBy ] = useState("Rank")

    useEffect(() => {
        let newArray = [...playersArray]
        for (let i = 0; i < newArray.length; i++){
            newArray[i].rank = i+1
        }
        console.log("whaat", newArray)
        setPlayers(newArray)
    }, [setPlayers])

    const openPlayer = (player) => {
        //const player = players.find(p => p.name === playerName)
        console.log(player)
    }

    const sort = (sortBy) => {
        let newArray = [...players]
        if (sortBy === "name"){
            if (currentSortBy === sortBy) newArray.sort((a, b) => b[sortBy] > a[sortBy] ? 1 : -1)
            else newArray.sort((a, b) => b[sortBy] < a[sortBy] ? 1 : -1)
        }
        else{
            if (currentSortBy === sortBy) newArray.sort((a, b) => a[sortBy] - b[sortBy])
            else newArray.sort((a, b) => b[sortBy] - a[sortBy])
        }
        if (currentSortBy === sortBy) setCurrentSortBy("")            
        else setCurrentSortBy(sortBy)
        setPlayers(newArray)
    }
    
    return <>
        <table className="content-table">
            <thead>
            <tr>
                <th onClick={() => sort("name")}>Player</th>
                <th>Rank</th>
                <th onClick={() => sort("rating")}>Smash Rating</th>
                <th>Main</th>
                <th>Secondary</th>
                <th onClick={() => sort("beerPong")}>Beer Pong Rating</th>
            </tr>
            </thead>
                <tbody>                        
                    { Array.isArray(players) && 
                        players.map((player, index) => (
                            <tr onClick={() => openPlayer(player)}>
                                <td>{player.name}</td>
                                <td>{player?.rank}</td>
                                <td>{player.rating}
                                    {player.placementGames > 0 &&
                                        <span className="placementGames">{player.placementGames}</span>
                                    }
                                </td>
                                <td>{player.main || ""}</td>
                                <td>{player.secondary || ""}</td>
                                <td>{player.beerPong || ""}</td>
                            </tr>
                        ))
                    }                          
                </tbody>
        </table>
    </>
}
