import React, { useEffect, useState, useRef } from "react";

export default function ThornamentBracket(){
    let groups = [
        [
            {
                "name": "unstoppables",
                "players": [
                    "Acrid",
                    "Mikka"
                ]
            },
            {
                "name": "Mikkelinch",
                "players": [
                    "Mikkel",
                    "Finch"
                ]
            },
            {
                "name": "Nah dude",
                "players": [
                    "Stockholm",
                    "Chagzter"
                ]
            }
        ],
        [
            {
                "name": "Not Rigged",
                "players": [
                    "Papi",
                    "Izizaki"
                ]
            },
            {
                "name": "DJ Wayne",
                "players": [
                    "YMG",
                    "DJJoel"
                ]
            },
            {
                "name": "How so many?",
                "players": [
                    "N4vid",
                    "Ztark"
                ]
            }
        ],
        [
            {
                "name": "MC Ganon",
                "players": [
                    "MC",
                    "Frudrunq"
                ]
            },
            {
                "name": "Classic",
                "players": [
                    "Oswalt",
                    "Mangz"
                ]
            },
            {
                "name": "Avvan 57",
                "players": [
                    "KP57",
                    "Avvan"
                ]
            }
        ],
        [
            {
                "name": "Barres kuk",
                "players": [
                    "Cpt. KöN",
                    "Barre"
                ]
            },
            {
                "name": "lis & lis",
                "players": [
                    "Miley",
                    "jarmo"
                ]
            },
            {
                "name": "Weirdos",
                "players": [
                    "Bagz",
                    "JukDk"
                ]
            }
        ]
    ]
    
    let uBracket = [
        {
            "team1": {
                "group": 4,
                "placement": "3"
            },
            "team2": {
                "group": 3,
                "placement": 2
            },
            "round": 0,
            "roundId": 0,
            "match": 1,
            "toLink": 0
        },
        {
            "team1": {
                "group": 3,
                "placement": 2
            },
            "team2": {
                "group": 2,
                "placement": 1
            },
            "round": 0,
            "roundId": 1,
            "match": 2,
            "toLink": 1
        },
        {
            "team1": {
                "group": 2,
                "placement": 1
            },
            "team2": {
                "group": 1,
                "placement": "3"
            },
            "round": 0,
            "roundId": 2,
            "match": 3,
            "toLink": 2
        },
        {
            "team1": {
                "group": 1,
                "placement": 4
            },
            "team2": {
                "group": 4,
                "placement": 3
            },
            "round": 0,
            "roundId": 3,
            "match": 4,
            "toLink": 3
        },
        {
            "team1": {
                "group": 4,
                "placement": 3
            },
            "round": 1,
            "roundId": 0,
            "match": 5,
            "toLink": 0
        },
        {
            "team1": {
                "group": 3,
                "placement": 2
            },
            "round": 1,
            "roundId": 1,
            "match": 6,
            "toLink": 0
        },
        {
            "team1": {
                "group": 2,
                "placement": 1
            },
            "round": 1,
            "roundId": 2,
            "match": 7,
            "toLink": 1
        },
        {
            "team1": {
                "group": 1,
                "placement": 4
            },
            "round": 1,
            "roundId": 3,
            "match": 8,
            "toLink": 1
        },
        {
            "round": 2,
            "roundId": 0,
            "match": 9,
            "toLink": 0
        },
        {
            "round": 2,
            "roundId": 1,
            "match": 10,
            "toLink": 0
        },
        {
            "round": 3,
            "roundId": 0,
            "match": 11,
            "toLink": 0
        }
    ]
    
    return <>
        <div className="bracketBody" style={{
            display: "flex",
            flexDirection:"column",
            justifyContent: "center"            
        }}>
            <div style={{display:"relative"}}>
            {groups && 
                groups.map(g => {
                    return <div >
                        <table style={{
                            border: "2px solid #333333"
                        }}>
                            <thead>
                                <tr>
                                    {
                                        [...[""], ...g].map(t => {
                                            return <th>{t.name}</th>
                                        })
                                    }
                                </tr>
                            </thead>
                    {
                        g.map((t, i) => {
                            return <tr>
                                <td>{t.name}</td>
                            </tr>
                        })
                    }
                    </table>          
                    </div>
                })
            }
            </div>
        </div>
    </>
}
