                            <td>
                                <div className="selectContainer">
                                    <div className="select">
                                        <select onChange={(e) => FindPlayer(3, e.target.value)}>
                                            {playerOptions()}
                                        </select>
                                    </div>
                                    <span>{getRating(3)}</span>
                                </div>
                            </td>
                            }
                        </tr>
                        {gameType !== "smash1" &&
                            <tr>
                                <td>Average Rating</td>
                                <td>{(getRating(2) + getRating(3)) / 2}</td>
                            </tr>
                        }
                        <tr>
                            <td>Win Percentage</td>
                            <td>{100 - winningPercentage}%</td>
                        </tr>
                    </table> 
                </div>
                <div className="selectContainer bestOf">
                    <div className="select">
                        <select id="bestOf" onChange={(e) => {
                            if (e.target.value == 1) setGamesWon([false])
                            if (e.target.value == 3) setGamesWon([false, false, false])
                            if (e.target.value == 5) setGamesWon([false, false, false, false, false])
                        }}>
                            <option value="1">Best of 1</option>
                            <option value="3" selected>Best of 3</option>
                            <option value="5">Best of 5</option>
                        </select>
                    </div>
                </div> 
                <div className="matchResults">
                    <table><tbody>
                        {
                            gamesWon.map((gameResult, i) => {
                                let foo = [...gamesWon]                                
                                foo.splice(i)

                                const lostGames = foo.reduce((pre, cur) => pre + (!cur && 1) , 0)
                                const wonGames = foo.reduce((pre, cur) => pre + (cur && 1) , 0)                                
                                let hideNext = false;

                                if (lostGames > gamesWon.length / 2 || wonGames > gamesWon.length / 2) hideNext = true

