import React from "react";
import { Link } from "react-router-dom";
import '../assets/style/Games.css'
import { Navbar } from '../components'

type gameType = {
    id: string,
    name: string,
    logo: string,
    info: string,
    min: number,
    max: number
}

export default function Games(){

    const games = [
        {"name": "Secret Hitler", "min": 5, "max": 10, "info": "abcsabd asdasd asdasd asdasda dsad asd asd asd"
            , "logo": "SH", "id":"SH"
        },
        {"name": "Castle Wars", "min": 2, "max": 2, "info": "abcsabd asdasd asdasd asdasda dsad asd asd asd"
            , "logo": "CW", "id":"CW"
        }
    ]

    function gameDiv(game: gameType){
        return <div className="card">
        {/* <img src="jeans3.jpg" alt="Denim Jeans" style="width:100%"> */}
        <h1>{game.name}</h1>
        <p className="price">{game.min === game.max ? game.max : game.min + ' - ' + game.max} players</p>
        <p>Some text about the jeans..</p>
        <p><button>Join Game</button></p>
        <p><button>Create Game</button></p>
      </div>
    }

    return <>
    <Navbar></Navbar>
    <div className="gameslist">
        {games.map(game => {
            return gameDiv(game)
        })}
    </div>        
    </>
}
