import React, { useEffect, useState } from "react";
//import { Radar } from "react-chartjs-2";
import usePlayers from "../../hooks/skrallgaming/usePlayers";
import playersArray from "../../globals/skrallgaming/players"
import placings from "../../globals/skrallgaming/placings"
import "../../assets/style/skrallgaming/thornament.css"

export default function Thornament(){

    let [ matches, setMatches ] = useState([
        {"winner":undefined, "name":"Match 1", "team1": "Fizzle My Nizzle", "team2": "Bramers", "bo": 3, "gameResults":[1, undefined, undefined]},
        {"winner":1,"name":"Winners' Semi Final","team1": "Fizzle My Nizzle", "team2": "NextGen", "bo": 3, "gameResults":[1, 0, 1]},
        {"winner":undefined,"name":"Losers' Semi Final","team1": "NextGen", "team2": "Bramers", "bo": 3, "gameResults":[0, 0, false]},
        {"winner":2,"name":"Winners' Final","team1": "IDK", "team2": "Deltalux", "bo": 3, "gameResults":[0, 1, 0]},
        {"winner":undefined,"name":"Losers' Final","team1": "Fizzle My Nizzle", "team2": "Bramers", "bo": 5, "gameResults":[1, 0 , 1, undefined, undefined]},
        {"winner":undefined,"name":"Grand Final","team1": "IDK", "team2": "Fizzle My Nizzle", "bo": 7, "gameResults":[undefined, undefined, undefined, undefined, undefined, undefined, undefined]}
    ])

    const teams = {
        "Fizzle My Nizzle": ["Mikka", "R"],
        "Bramers": ["Acrid", "Lexclusive"],
        "NextGen": ["Barre", "YOLOwille"],
        "IDK": ["Zeor", "Finch"],
        "Deltalux": ["Eb0la", "Avvan"],
        "Raindrops": ["JÃ¤ger", "Jarmo"],
    }
    
    const teamColor = (teamName) => {
        // let hash = 0;
        // for (let i = 0; i < teamName.length; i++) {
        //     hash = teamName.charCodeAt(i) + ((hash << 5) - hash);
        //     hash = hash & hash;
        // }
        // return hash % 360
        let teamsArr = Object.keys(teams)
        let c = teamsArr.findIndex(t => t === teamName)
        return Math.round(c / teamsArr.length * 360)
    }

    const updateGameResult = (matchIndex, roundIndex, winner) => {
        // winner = empty, team1, team2
        let arr = [...[], ...matches]
        const bo = matches[matchIndex].bo
        let won = 0; let lost = 0

        if (roundIndex === null)
            roundIndex = arr[matchIndex].gameResults.findIndex(m => m === undefined) || 0

        if (winner === "empty"){
            for (let i = roundIndex; i < bo ; i++){
                arr[matchIndex].gameResults[i] = undefined    
            }
        }
        
        for (let i = 0; i < bo; i++){
            if (won > bo/2 || lost > bo/2){                
                arr[matchIndex].gameResults[i] = false
                continue
            }
            if (i === roundIndex) arr[matchIndex].gameResults[roundIndex] = (winner === "team1" ? 1 : (winner === "team2" ? 0 : undefined))
            if (arr[matchIndex].gameResults[i]) won += 1;
            if (arr[matchIndex].gameResults[i] === 0) lost += 1;
        }
        arr[matchIndex].winner = won > bo/2 ? 1 : lost > bo/2 ? 2 : undefined;
        setMatches(arr)
    }

    return <>        
        <div className="body">
            <div className="ulkeeper">
                <ul>
                    {(console.log("matches:", matches) || matches) &&
                        matches.map((match, index) => {
                            return <li>
                                    <span className="matchcount">{match.name}</span>
                                    <div className="teambody" onClick={() => {updateGameResult(index, null, "team1")}} 
                                    style={{
                                        background: `hsl(${teamColor(match.team1)}, 100%, 24%)`,
                                        boxShadow: `inset 12px 12px 12px hsl(${teamColor(match.team1)}, 100%, 15%), inset -12px -12px 12px hsl(${teamColor(match.team1)}, 100%, 29%)`,
                                        opacity: `${match.winner === 2 ? 35 : 100}%`
                                    }}>
                                        <div className="teamplayers">
                                            {
                                                teams[match.team1].map(p => {                                                    
                                                    return <p>{p}</p>
                                                })
                                                //teams[match[0]].map(p => {console.log(p)})
                                            }
                                        </div>
                                        <div className="teamname ">{match.team1}</div>
                                    </div>
                                    <div className="scorebody">{match?.gameResults &&
                                        [...Array(match.bo)].map((_, i ) => {
                                            let gr = match.gameResults?.[i]
                                            if (gr === 0) gr = 2

                                            if (gr === undefined) return <div className="score"></div>
                                            if (gr === false) return <div className="score" style={{opacity:"0%"}}></div>
                                            return <div className="score" style={{
                                                background: `hsl(${teamColor(match[`team${gr}`])}, 100%, 24%)`,
                                                boxShadow: `inset 8px 8px 8px hsl(${teamColor(match[`team${gr}`])}, 100%, 15%), inset -8px -8px 8px hsl(${teamColor(match[`team${gr}`])}, 100%, 29%)`,                                                
                                            }}
                                                onClick={() => {updateGameResult(index, i, "empty")}}
                                            ></div>
                                        })
                                    }</div>
                                    <div className="teambody" onClick={() => {updateGameResult(index, null, "team2")}}
                                    style={{
                                        background: `hsl(${teamColor(match.team2)}, 100%, 24%)`,
                                        boxShadow: `inset 12px 12px 12px hsl(${teamColor(match.team2)}, 100%, 15%), inset -12px -12px 12px hsl(${teamColor(match.team2)}, 100%, 29%)`,
                                        opacity: `${match.winner === 1 ? 35 : 100}%`
                                    }}>
                                        <div className="teamname">{match.team2}</div>
                                        <div className="teamplayers">
                                            {
                                                teams[match.team2].map(p => {
                                                    return <p>{p}</p>
                                                })
                                                //teams[match[0]].map(p => {console.log(p)})
                                            }
                                        </div>
                                    </div>           
                            </li>
                        })
                    }
                </ul>
            </div>
        </div>
    </>
}
