import React, { useEffect, useState } from "react";
//import { Radar } from "react-chartjs-2";
import usePlayers from "../../hooks/skrallgaming/usePlayers";
import playersArray from "../../globals/skrallgaming/players"
import placings from "../../globals/skrallgaming/placings"
import "../../assets/style/skrallgaming/thornament.css"

export default function Thornament(){

    const teams = {
        "Fizzle My Nizzle": ["Mikka", "R"],
        "Bramers": ["Acrid", "Lexclusive"],
        "Fuckbois": ["Barre", "YOLOwille"],
    }

    const matches = [
        ["Fizzle My Nizzle", "Bramers"],
        ["Fizzle My Nizzle", "Fuckbois"],
        ["Bramers", "Fuckbois"],
    ]
    
    return <>        
        <div className="body">
            <div className="ulkeeper">
                <ul>
                    {
                        matches.map(match => {
                            return <li>
                                    <div className="teambody" style={{
                                        background: "linear-gradient(145deg, #006506, #007807)",
                                        boxShadow: "inset 12px 12px 12px hsl(124, 100%, 15%), inset -12px -12px 12px hsl(124, 100%, 29%)"   
                                    }}>
                                        <div className="teamplayers">
                                            {
                                                teams[match[0]].map(p => {
                                                    return <p>{p}</p>
                                                })
                                                //teams[match[0]].map(p => {console.log(p)})
                                            }
                                        </div>
                                        <div className="teamname ">{match[0]}</div>
                                    </div>
                                    <div className="scorebody"><div className="score input">0 - 0</div></div>
                                    <div className="teambody">
                                        <div className="teamname">{match[1]}</div>
                                        <div className="teamplayers">
                                            {
                                                teams[match[1]].map(p => {
                                                    return <p>{p}</p>
                                                })
                                                //teams[match[0]].map(p => {console.log(p)})
                                            }
                                        </div>
                                    </div>           
                            </li>
                        })
                    }
                </ul>
            </div>
        </div>
    </>
}
